mapper.select:


		select * from(           
	    	select rownum rn, board.* from	
	            (select * from board where board_category_num = #{board_category_num} order by group_num desc, orders)
	            board where rownum <= #{pageNum} * #{amount}
	            )
        where rn > (#{pageNum} - 1) * #{amount}

mapper.insert:
select seq_board.nextval from dual
insert into board ( board_num, member_id, member_name, subject, content, group_num, board_category_num, pwd, product_num
) values ( #{board_num}, #{member_id}, #{member_name}, #{subject}, #{content}, #{board_num}, #{board_category_num}, #{pwd}, #{product_num} )

mapper.delete:
begin
	delete reply where board_num = #{board_num};
	delete board where board_num = #{board_num};
end;
